var makeRow=function(n){for(var t=[],e=0;e<n;e++)t.push("A");return t},addInX=function(n){var t=n,e=randomNumber(t[0].length),o=randomNumber(t.length);t[e][o]="X",convertToString(t)},makeColumn=function(n,t){for(var e=[],o=0;o<t;o++)e.push(makeRow(n));addInX(e)},randomNumber=function(n){var t=Math.floor(Math.random()*(n-1));return t},convertToString=function(n){for(var t=[],e=0;e<n.length;e++)t+="["+n[e].join()+"] \n";printOutToDom.innerHTML="[\n"+t+"]"},makeFakeMap=function(n,t){makeColumn(n,t)},activate=document.querySelector(".activate"),rowInputs=document.querySelector(".row"),columnInputs=document.querySelector(".column"),printOutToDom=document.querySelector(".printOut");activate.addEventListener("click",function(){makeFakeMap(parseInt(rowInputs.value),parseInt(columnInputs.value))});